    private void pilihpantaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pilihpantaiActionPerformed
        String pilihpemandu [] = {"Nama Pemandu"};
        String pantaiSI [] = {"Pilih Pemandu","Muhammad Adi","Rian","Doni"};
        String pantaiML [] = {"Pilih Pemandu","Adi Akbar","Rani","Rony"};
        String pantaiPP [] = {"Pilih Pemandu","Solihin","Ami","Anjar"};
        String pantaiJung [] = {"Pilih Pemandu","Cristian Ali","Ali Jadi","Covidian"};
        switch (pilihpantai.getSelectedIndex()){

            case 1:
                PilihPemandu.removeAllItems();
                for (int i = 0; i < pantaiSI.length; i++) {
                    PilihPemandu.addItem(pantaiSI[i]);
                }
                break;
            case 2:
                PilihPemandu.removeAllItems();
                for (int i = 0; i < pantaiPP.length; i++) {
                    PilihPemandu.addItem(pantaiPP[i]);
                }
            break;
            case 3:
                PilihPemandu.removeAllItems();
                for (int i = 0; i < pantaiML.length; i++) {
                    PilihPemandu.addItem(pantaiML[i]);
                }
            break;
            case 4:
                PilihPemandu.removeAllItems();
                for (int i = 0; i < pantaiJung.length; i++) {
                    PilihPemandu.addItem(pantaiJung[i]);
                }
            break;
            default:
                
        }
        
        
        
    }//GEN-LAST:event_pilihpantaiActionPerformed

    private void PesanPantaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PesanPantaiActionPerformed
        // TODO add your handling code here:
            int i = nomor.getText().length();
        if (i<11 || i>13) {
           javax.swing.JOptionPane.showMessageDialog(null,"Masukkan Nomor Dengan Benar");
        } else {
            javax.swing.JOptionPane.showMessageDialog(null,"Pemesanan Berhasil, Tunggu Admin Mengirim Pesan Kepada Anda Untuk Transaksi Dll yaaaa");
        }
    }//GEN-LAST:event_PesanPantaiActionPerformed

